import React from 'react';
import styled from 'styled-components';
import NewsData from '../NewsData';

const CardContainer = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  width: 100%;
  box-sizing: border-box;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-evenly;
  margin-bottom: 30px;
  .card-item {
    width: 320px;
    flex: none;
    border: 1px solid #d5d5d5;
    margin: 10px 5px;
    .list-item-link {
      box-sizing: border-box;
      width: 100%;
      display: flex;
      flex-wrap: nowrap;
      flex-direction: column;
      align-items: center;
      text-decoration: none;
      color: #000;
      transition: all 0.1s;
      &:hover {
        background-color: #eeeeee55;
      }
      .thumbnail {
        width: 100%;
        height: 15px;
        display: block;
        object-fit: cover;
        flex: none;
      }
      .content {
        flex: 0 1 auto;
        display: flex;
        flex-direction: column;
        align-content: flex-start;
        padding: 10px 15px;
        background-color: #ff01;
        h3 {
          background-color: #f0f1;
          box-sizing: border-box;
          font-size: 18px;
          font-weight: bold;
          margin: 0;
          margin-bottom: 10px;
          display: -webkit-box;
          overflow: hidden;
          text-overflow: ellipsis;
          -webkit-line-clamp: 2;
          -webkit-box-orient: vertical;
        }
      }
    }
  }
`;
const NewsCard = () => {
  return (
    <div>NewsCard</div>
  );
};

export default NewsCard;
