import React from 'react';
import styled from 'styled-components';
import NewsData from '../NewsData';
import breakpoints from 'styled-components-breakpoints';


// 반응형 웹 구현 기준 사이즈 정의
const sizes = {
  sm: 600,
  md: 768,
  lg: 992,
  xl: 1200
};

// 기준 사이즈를 활용하여 media query 생성
const mq = breakpoints(sizes);

const CardContainer = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  width: 100%;
  box-sizing: border-box;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-evenly;
  .card-item {
    width: 100%;
    box-sizing: border-box;
    padding: 0 5px;
    margin: 10px 0;
    
    ${mq.minWidth('sm')`width: 50%`}

    // 중간 해상도(태블릿 가로)
    ${mq.minWidth('md')`width: 33.3%`}

    // 큰 해상도(노트북,데스크탑)
    ${mq.minWidth('lg')`width: 25%`}

    // 초 고해상도
    ${mq.minWidth('xl')`width: 20%`}
    
    .list-item-link {
      box-sizing: border-box;
      width: 100%;
      display: flex;
      flex-wrap: nowrap;
      flex-direction: column;
      align-items: center;
      text-decoration: none;
      color: #000;
      transition: all 0.1s;
      &:hover {
        background-color: #eeeeee55;
      }
      .thumbnail {
        width: 100%;
        height: 15px;
        display: block;
        object-fit: cover;
        flex: none;
      }
      .content {
        flex: 0 1 auto;
        display: flex;
        flex-direction: column;
        align-content: flex-start;
        padding: 10px 15px;
        background-color: #ff01;
        h3 {
          background-color: #f0f1;
          box-sizing: border-box;
          font-size: 18px;
          font-weight: bold;
          margin: 0;
          margin-bottom: 10px;
          display: -webkit-box;
          overflow: hidden;
          text-overflow: ellipsis;
          -webkit-line-clamp: 2;
          -webkit-box-orient: vertical;
        }
        p {
          background-color: #0601;
          font-size: 14px;
          margin: 0;
          margin-bottom: 8px;
          display: -webkit-box;
          overflow: hidden;
          text-overflow: ellipsis;
          -webkit-line-clamp: 3;
          -webkit-box-orient: vertical;
        }
        ul {
          background-color: #0061;
          list-style: none;
          padding: 0;
          margin: 0;
          li {
            display: inline-block;
            font-size: 12px;
            &:first-child:after {
              content: '|';
              display: inline-block;
              color: #555;
              padding: 0 5px;
            }
          }
        }
      }
    }
  }
`;
const NewsCard = () => {
  console.clear();
  return (
    <div>
      <CardContainer>
        {NewsData.map((v,i) => {
          const {url,image,title,description,author,datetime} = v;
          return (
            <li className="card-item" key={i}>
              <a className="list-item-link" href={url} target="_blank" rel="noreferrer">
                <img className="thumbnail" src={image} alt="thumbnail"/>
                <div className="content">
                  <h3>{title}</h3>
                  <p>{description}</p>
                  <ul>
                    <li>{author}</li>
                    <li>{new Date(datetime).toLocaleString()}</li>
                  </ul>
                </div>
              </a>
            </li>
          );
        })}
      </CardContainer>
    </div>
  );
};

export default NewsCard;
