import React from 'react';
import styled,{css} from 'styled-components';

/* 컴포넌트 코드 안에서 SCSS 문법을 적용한 컴포넌트를 직접 정의
- 패키지 설치 필요
    yarn add styled-components
- vscode에서 styled-components 지원을 위한 확장프로그램
    vscode-styled-components */
const MyGridContainer = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  width: 1024px;
  border: 5px solid #cc0;
  padding: 10px;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
`;

const MyGridItem = styled.li`
  /* width: 33%; */
`;

const MyBox = styled.div`
  border: 3px dotted #000;
  background-color: #eee;
  height: 100px;
  text-align: center;
  font-size: 20px;
  line-height: 100px;
  cursor: pointer;
  transition: all 0.1s ease-in;
  color: #000;
  &:hover {
    transform: scale(1.05,1.05) rotate(-10deg);
    color: #fff;
  }
`;

const StyledComponent = () => {
  return (
    <div>
      <h2>StyledComponent</h2>
      <h3>단순 태그처럼 사용</h3>
      {/* MyGridItem에게 width라는 이름의 변수값 전달 */}
      <MyGridContainer>
        <MyGridItem>
          <MyBox>Item1</MyBox>
        </MyGridItem>
        <MyGridItem>
          <MyBox>Item2</MyBox>
        </MyGridItem>
        <MyGridItem>
          <MyBox>Item3</MyBox>
        </MyGridItem>
        <MyGridItem>
          <MyBox>Item4</MyBox>
        </MyGridItem>
        <MyGridItem>
          <MyBox>Item5</MyBox>
        </MyGridItem>
      </MyGridContainer>
    </div>
  );
};

export default StyledComponent;
