import React from 'react';
import styled from 'styled-components';
import NewsData from '../NewsData';

const CardContainer = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  width: 100%;
  box-sizing: border-box;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-evenly;
  margin-bottom: 30px;
  .card-item {
    width: 320px;
    flex: none;
    border: 1px solid #d5d5d5;
    margin: 10px 5px;
    .list-item-link {
      box-sizing: border-box;
      width: 100%;
      display: flex;
      flex-wrap: nowrap;
      flex-direction: column;
      align-items: center;
      text-decoration: none;
      color:#000;
    }
  }
`;
const NewsCard = () => {
  return (
    <div>NewsCard</div>
  );
};

export default NewsCard;
