import React from 'react';
import styled from 'styled-components';

const ListItem = styled.li`
  border-top: 1px solid #eee;
  &:last-child {
    border-bottom: 1px solid #eee;
  }
  .list-item-link {
    box-sizing: border-box;
    display: flex;
    flex-wrap: nowrap;
    flex-direction: row;
    align-items: center;
    padding: 5px 10px;
    text-decoration: none;
    color: #000;
    transition: all 0.1s;
    &:hover {
      background-color: #eeeeee55;
    }
    .thumbnail {
      width: 150px;
      height: 100px;
      display: block;
      object-fit: cover;
      flex-none;
    }
    .content {
      flex: 0 1 auto;
      padding: 5px 0 5px 20px;
      background-color: #ff01;
      h3 {
        background-color: #f0f1;
        box-sizing: border-box;
        font-size: 18px;
        font-weight: bold;
        margin: 0;
        margin-bottom: 10px;
        display: -webkit-box;
        overflow: hidden;
        text-overflow: ellipsis;
        -webkit-line-clamp: 1;
        -webkit-box-orient: vertical;
      }
      
    }
  }
`;

const NewsItem = () => {
  return (
    <div>NewsItem</div>
  );
};

export default NewsItem;
