import React from 'react';
import styled from 'styled-components';
import mq from '../../MediaQuery';
import JbtImg from './img/hamburger.jpg';

const JumbotronContainer = styled.div`
    width: 100%;
    figure {
            display: flex;
            position: relative;
    }
    figcaption {
      position: absolute;
      left: 25px;
      bottom: 30px;

      h1 {
        font-size: 38px;
        letter-spacing: 0.1em;
        color: rgb(100,100,100);
        font-weight: 400;
      }
      h5 {
        font-size: 22px;
        margin-top: 30px;
        margin-bottom: 20px;
      }
    }
    ${mq.maxWidth('sm')`
        flex-direction: column-reverse;
    `}
`;


const Main = () => {
  return (
    <JumbotronContainer>
        <div>
            <figure>
            <JImg className='j_img' />
            <figcaption><h1>Le Catering</h1></figcaption>
            </figure> 
        </div>
    </JumbotronContainer>
  );
};

export default Main;
