import React from 'react';

const PrintStar = () => {
  const [rowNum, setRowNum] = React.useState(0);
  
  

  const onInputChange = React.useCallback(e => {
    for (let i=0; i<rowNum; i++) {
      let str=""; // 매 행마다 초기화
      for (let j=0; j<i+1; j++) {
          str += "*";
      }
      console.log(str);
    }
  },[]);

  return (
    <div>
      <h2>PrintStar</h2>
      <input type='text' placeholder='input...' onChange={onInputChange} />
      <h3>{str}</h3>
    </div>
  );
};

export default PrintStar;
