import React from 'react';

const PrintStar = () => {
  const [rowNum, setRowNum] = React.useState();
  const console = React.useRef();
  
  const onInputChange = React.useCallback(e => {
    const max = setRowNum(e.currentTarget.value);
    for (let i=0; i<max; i++) {
      let rowNum=""; 
      for (let j=0; j<i+1; j++) {
        rowNum += "*";
      }
    }
  },[]);

  return (
    <div>
      <h2>PrintStar</h2>
      <input type='text' placeholder='input...' onChange={onInputChange} />
      {/* <h3>{rowNum}</h3> */}
    </div>
  );
};

export default PrintStar;
