import React from 'react';

const x = React.useRef();
const y = React.useRef();
const exec = React.useRef();

function getResultValue(state,action) {
  const X = parseInt(x.current.value);
  const Y = parseInt(y.current.value);
  switch (action) {
    case 'PLUS':
      return X+Y;
    case 'MINUS':
      return X-Y;
    case 'SUM':
      return X*Y;
    case 'DIV':
      return X/Y;
  }
}

const Calc = () => {
  const [resultValue, setResultValue] = React.useReducer(getResultValue,0);

  React.useMemo(() => {
    if (resultValue % 2 == 0) {
      resultValue.style.color = 'red';
    } else {
      resultValue.style.color = 'blue';
    }
  },[resultValue]);

  return (
    <div>
      <h2>Calc</h2>
      <p>useReducer, useMemo, useCallback을 활용한 사칙연산</p>
      <input type='text' ref={x} />
      <select ref={exec}>
        <option value="">+</option>
        <option value=""></option>
        <option value=""></option>
        <option value=""></option>
      </select>
      <input type='text' ref={y} />
    </div>
  );
};

export default Calc;
