import React from 'react';

const PrintStar = () => {
  const [rowNum, setRowNum] = React.useState();
  const console = React.useRef();

  React.useEffect(e => {
    const max = setRowNum(e.currentTarget.value);
    for (let i=0; i<max; i++) {
      let str=""; 
      for (let j=0; j<i+1; j++) {
        str += "*";
      }
      console.current.innerHTML = str;
    }
  },[rowNum]);

  const onInputChange = () => (e => {
    setRowNum(e.currentTarget.value);
  });

  return (
    <div>
      <h2>PrintStar</h2>
      <p>useState, useEffect, useRef를 사용한 별찍기 구현</p>
      <label htmlFor="rowNumInput">rownum:  
      <input type='text' placeholder='input...' value={rowNum} onChange={onInputChange} />
      </label>
      <h3>{console}</h3>
    </div>
  );
};

export default PrintStar;
