import React from 'react';

const Calc = () => {
  const [resultValue, setResultValue] = React.useReducer(getResultValue,0);

  React.useMemo(() => {
    if (resultValue % 2 == 0) {
      resultValue.style.color = 'red';
    } else {
      resultValue.style.color = 'blue';
    }
  },[resultValue]);

  return (
    <div>
      <h2>Calc</h2>
      <p>useReducer, useMemo, useCallback을 활용한 사칙연산</p>
      <input type='text' ref={x} />
      <select ref={exec}>
        <option value="PLUS">+</option>
        <option value="MINUS">-</option>
        <option value="SUM">*</option>
        <option value="DIV">/</option>
      </select>
      <input type='text' ref={y} />
    </div>
  );
};

export default Calc;
