import React,{memo,useCallback} from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import btn from '../assets/img/btn.png';
import btnOver from '../assets/img/btn_over.png';

const MenuContainer = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  
  .link {
    display: block;
    width: 179px;
    height: 48px;
    background: url(${btn});
    line-height: 48px;
    text-align: center;
    font-weight: bold;
    color: #cfdfb5;
    text-decoration: none;

    &:hover {
      background: url(${btnOver});
    }
  }
  .menu-item {
    position: relative;

    .sub {
      list-style: none;
      margin: 0;
      padding: 0;
      position: absolute;
      z-index: 1000;
      max-height: 0;
      overflow: hidden;
      transition: max-height 180ms ease-out;
    }
  }
`;

const SubmenuEx = memo(() => {
  const onMenuItemOver = useCallback((e) => {
    const current = e.currentTarget;
    const sub = current.querySelector('.sub');

    // scrollHeight는 요소의 크기를 벗어난만큼의 높이를 의미
    sub.style.maxHeight = sub.scrollHeight + 'px';
  },[]);

  const onMenuItemOut = useCallback((e) => {
    const current = e.currentTarget;
    const sub = current.querySelector('.sub');

    // scrollHeight는 요소의 크기를 벗어난만큼의 높이를 의미
    sub.style.maxHeight = null;
  },[]);

  return (
    <div>
      <h2>SubmenuEx</h2>
      <MenuContainer>
        <li className='menu-item' onMouseOver={onMenuItemOver} onMouseOut={onMouseOut}>
          <Link to='#' className='link'>HTML+CSS</Link>
          <Link to='#' className='link'>Javascript</Link>
          <Link to='#' className='link'>jQuery</Link>
          <ul>
          <Link to='#' className='link'>HTML+CSS</Link>
            <li></li>
          </ul>
        </li>
      </MenuContainer>
    </div>
  );
});

export default SubmenuEx;
