import React,{memo} from 'react';
import axios from 'axios';
import Spinner from '../components/Spinner';

const Department = memo(() => {
  // 현재 ajax가 데이터를 로딩중인지를 의미하는 상태값
  const [loading, setLoading] = React.useStat(false);
  // 화면에 표시할 상태값(ajax 연동 결과로 받아올 json) -> 초기값을 빈 배열로 정의
  const [department, setDepartment] = React.useState([]);
  // // 검색 키워드
  // const [keyword, setKeyword] = React.useState('');
  // // 삭제할 항목에 대한 id값을 저장하기 위한 상태값
  // const [dropId, setDropId] = React.useState(-1);
  // // 수정할 항목에 대한 id값을 저장하기 위한 상태값
  // const [updateId, setUpdateId] = React.useState(-1);

  // 페이지가 처음 열렸을 때 실행할 hook
  React.useEffect(() => {
    // ajax 로딩 시작을 알림
    setLoading(true);

    setTimeout(() => {
      (async () => {
        try {
          const response = await axios.get("/department/");
          // 일반 상태값 업데이트
          // setDepartment(response.data);
          
          // 함수형 업데이트
          setDepartment(department => response.data);
        } catch (e) {
          console.error(e);
          alert('Ajax 연동 실패');
          return;
        } finally {
          setLoading(false);
        }
      })();
    },500);
  },[]);

  return (
    <div>
      <Spinner visible={loading} />
      <h2>학과목록</h2>
      <hr />
      <table border='1'>
        <thead>
          <tr>
            <th>학과번호</th>
            <th>학과명</th>
            <th>학과위치</th>
          </tr>
        </thead>
        <tbody>
          {department.length > 0 ? (
            department.map((item,index) => {
              return (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.dname}</td>
                  <td>{item.loc}</td>
                </tr>
              );
            })
          ) : (
            <tr>
              <td colSpan='3' align='center'>
                검색 결과가 없습니다.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
});

export default React.memo(Department);
