const Test = memo(() => {
    const dispatch = useDispatch();
    const {data,loading,error} = useSelector((state) => state.DepartmentSlice);

    useEffect(() => {
        dispatch(getList());

        // dispatch(getItem({id:101}));
        dispatch(postItem({dname:'react.js',id}))
    },[dispatch]);
    
    return (
        loading ? 'loading...' : (
            error ? JSON.stringify(error) : (
                <>
                    {JSON.stringify(data)}
                </>
            )
        )
    );
});

export default Test;
